import { useNavigate, Form, useNavigation, useActionData } from 'react-router-dom';

import classes from './EventForm.module.css';

function EventForm({ method, event }) {
   const data = useActionData();
  const navigate = useNavigate();
  const navigation = useNavigation();

  const isSubmitting = navigation.state === 'submitting'

  function cancelHandler() {
    navigate('..');
  }
//data && data.errors: Bu ifade, data değişkeninin var olup olmadığını kontrol eder. 
//Object.values(data.errors): Bu ifade, data.errors içindeki hataları temsil eder.

  return (
    <Form method='POST' className={classes.form}>
         {data && data.errors && <ul> 
        {Object.values(data.errors).map(err => <li key={err}>{err}</li>)}
        </ul>} 
      <p>
        <label htmlFor="title">Title</label>
        <input id="title"
         type="text"
          name="title" 
          required 
          defaultValue={event ? event.title : ''}
           />
      </p>
      <p>
        <label htmlFor="image">Image</label>
        <input id="image"
         type="url" 
         name="image" 
         required 
         defaultValue={event ? event.image : ''}
          />
      </p>
      <p>
        <label htmlFor="date">Date</label>
        <input id="date"
         type="date"
          name="date"
           required
            defaultValue={event ? event.date : ''} />
      </p>
      <p>
        <label htmlFor="description">Description</label>
        <textarea id="description"
         name="description" 
         rows="5" 
         required 
         defaultValue={event ? event.description : ''} />
      </p>
      <div className={classes.actions}>
        <button type="button" 
        onClick={cancelHandler} 
        disabled={isSubmitting}>
          Cancel
        </button>
        <button disabled={isSubmitting}>
          {isSubmitting ? 'Onaylanıyor' : 'Kaydet'}
          </button>
      </div>
    </Form>
  );
}

export default EventForm;
